        M_c_cae__a_abb -= 1/12 * einsum('IK,xyzB,xwuv,Yzuwyv->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/12 * einsum('IK,xyzB,xwuv,Yzuyvw->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 5/12 * einsum('IK,xyzB,xwuv,Yzuywv->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/6 * einsum('IK,xyzB,ywuv,Yzuvwx->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/6 * einsum('IK,xyzB,ywuv,Yzuvxw->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/6 * einsum('IK,xyzB,ywuv,Yzuwvx->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/3 * einsum('IK,xyzB,ywuv,Yzuwxv->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/6 * einsum('IK,xyzB,ywuv,Yzuxvw->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb -= 1/6 * einsum('IK,xyzB,ywuv,Yzuxwv->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/2 * einsum('IK,xyzB,ywxu,Yzwu->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_ccaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/24 * einsum('IK,xyzB,zwuv,Ywvuxy->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/24 * einsum('IK,xyzB,zwuv,Ywvuyx->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/24 * einsum('IK,xyzB,zwuv,Ywvxuy->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cae__a_abb += 1/24 * einsum('IK,xyzB,zwuv,Ywvxyu->IKYB', np.identity(ncvs), t1_aaae, v_aaaa, rdm_cccaaa, optimize = einsum_type)
