        e_cvs = mr_adc.mo_energy.x

        ## One-electron integrals
        h_aa = mr_adc.h1eff.aa

        h_xa = mr_adc.h1eff.xa

        ## Two-electron integrals
        v_aaaa = mr_adc.v2e.aaaa
        
