        v_xaaa = mr_adc.v2e.xaaa

        ## Amplitudes
        t1_xa = mr_adc.t1.xa

        t1_xaaa = mr_adc.t1.xaaa

        # Reduced Density Matrices
        rdm_ca = mr_adc.rdm.ca
        rdm_ccaa = mr_adc.rdm.ccaa
