        M_c_cca__a_abb += 1/2 * einsum('IK,Lxyz,Yzwy,xw->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_ca, optimize = einsum_type)
        M_c_cca__a_abb += 1/24 * einsum('IK,Lxyz,xwuv,Ywvuyz->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/24 * einsum('IK,Lxyz,xwuv,Ywvuzy->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/24 * einsum('IK,Lxyz,xwuv,Ywvyuz->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 11/24 * einsum('IK,Lxyz,xwuv,Ywvyzu->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/24 * einsum('IK,Lxyz,xwuv,Ywvzuy->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/24 * einsum('IK,Lxyz,xwuv,Ywvzyu->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/6 * einsum('IK,Lxyz,ywuv,Yxuvwz->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/6 * einsum('IK,Lxyz,ywuv,Yxuvzw->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/6 * einsum('IK,Lxyz,ywuv,Yxuwvz->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/3 * einsum('IK,Lxyz,ywuv,Yxuwzv->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/6 * einsum('IK,Lxyz,ywuv,Yxuzvw->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/6 * einsum('IK,Lxyz,ywuv,Yxuzwv->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 1/2 * einsum('IK,Lxyz,ywzu,Yxwu->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_ccaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/12 * einsum('IK,Lxyz,zwuv,Yxuvwy->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/12 * einsum('IK,Lxyz,zwuv,Yxuvyw->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/12 * einsum('IK,Lxyz,zwuv,Yxuwvy->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/12 * einsum('IK,Lxyz,zwuv,Yxuwyv->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb -= 1/12 * einsum('IK,Lxyz,zwuv,Yxuyvw->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)
        M_c_cca__a_abb += 5/12 * einsum('IK,Lxyz,zwuv,Yxuywv->IKLY', np.identity(ncvs), t1_xaaa, v_aaaa, rdm_cccaaa, optimize = einsum_type)

        M_c_cca__a_aaa = M_c_cca__a_abb - M_c_cca__a_abb.transpose(0,2,1,3)

        ## Reshape tensors to matrix form
        M_c_cca__a_aaa = M_c_cca__a_aaa[:, cvs_tril_ind[0], cvs_tril_ind[1]]

        M_c_cca__a_aaa = M_c_cca__a_aaa.reshape(ncvs, -1)
        M_c_cca__a_abb = M_c_cca__a_abb.reshape(ncvs, -1)

        ## Building C-CCA matrix
        s_aaa = 0
