        einsum = mr_adc.interface.einsum
        einsum_type = mr_adc.interface.einsum_type

        # Variables from kernel
        ncvs = mr_adc.ncvs

        # Amplitudes
        t1_xe = mr_adc.t1.xe

        t1_xaea = mr_adc.t1.xaea
        t1_xaae = mr_adc.t1.xaae

        t1_xa = mr_adc.t1.xa

